import React, { Component } from "react";

const TOKEN = "z0gq5xnnz5l5wuyb1yissddi0khmpaayei3gkrv6";
const BASEURL =
  "https://stocknewsapi.com/api/v1/category?section=general&items=50&token=";

class News extends Component {
  constructor(props) {
    super(props);
    this.state = {
      news: [],
      isNewsLoaded: false
    };
  }

  componentDidMount() {
    fetch(BASEURL + TOKEN)
      .then(res => res.json())
      .then(json => {
        this.setState({
          isNewsLoaded: true,
          news: json
        });
      });
  }

  render() {
    var { isNewsLoaded, news } = this.state;
    if (!isNewsLoaded) {
      return <div>Loading...</div>;
    } else {
      return;
    }
  }
}

export default News;
