import React, { Component } from "react";
import ReactCardFlip from "react-card-flip";
import NavBar from "./components/navbar";
import {
  Card,
  CardImg,
  CardText,
  CardBody,
  CardTitle,
  CardSubtitle,
  Button
} from "reactstrap";

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      items: [],
      isLoaded: false,
      isFlipped: false
    };
    this.handleClick = this.handleClick.bind(this);
  }

  componentDidMount() {
    fetch(
      "https://cloud.iexapis.com/beta/stock/market/list/gainers?token=pk_b24a0719ee7f4415b33737e51e8ef7f8"
    )
      .then(res => res.json())
      .then(json => {
        this.setState({
          isLoaded: true,
          items: json
        });
      });
  }

  handleClick(event) {
    event.preventDefault();
    this.setState(prevState => ({ isFlipped: !prevState.isFlipped }));
  }

  render() {
    var { isLoaded, items } = this.state;
    if (!isLoaded) {
      return <div>Loading...</div>;
    } else {
      return (
        <React.Fragment>
          <NavBar />
          <main className="container">
            <ul class="unstyled">
              {items.map(item => (
                <Card>
                  <ReactCardFlip
                    isFlipped={this.state.isFlipped}
                    flipDirection="vertical"
                  >
                    <li key="front">
                      <li key={item.symbol}>
                        <CardBody>
                          <CardTitle>Stock: {item.symbol}</CardTitle>
                          Latest Price: {item.latestPrice}
                          <Button onClick={this.handleClick}>
                            Click to flip
                          </Button>
                        </CardBody>
                      </li>
                    </li>

                    <li key="back">
                      <li key={item.symbol}>
                        <CardBody>
                          <CardTitle>
                            {" "}
                            Company Name: {item.companyName}{" "}
                          </CardTitle>
                          High: {item.high} | Low: {item.low}
                          <Button onClick={this.handleClick}>
                            Click to flip
                          </Button>
                        </CardBody>
                      </li>
                    </li>
                  </ReactCardFlip>
                </Card>
              ))}
            </ul>
          </main>
        </React.Fragment>
      );
    }
  }
}

export default App;
