import React, { Component } from "react";
import ReactCardFlip from "react-card-flip";
import styles from "../index.css";
import { Card, CardBody, Button, CardHeader } from "reactstrap";

const BASEURL = "https://cloud.iexapis.com/v1/stock/market/batch?symbols=";
const TOKEN = `${process.env.REACT_APP_IEX_KEY}`;

class Search extends Component {
  constructor(props) {
    super(props);
    this.state = {
      query: "",
      results: [],
      isFlipped: false,
      isLoaded: false
    };
    this.handleClick = this.handleClick.bind(this);
  }

  getInfo = () => {
    fetch(BASEURL + `${this.state.query}` + "&types=quote&token=" + TOKEN)
      .then(res => res.json())
      .then(json => {
        let toString = "[" + JSON.stringify(json) + "]";
        let toJSON = JSON.parse(toString);
        this.setState({
          results: toJSON,
          isLoaded: true
        });
      });
  };

  handleInputChange = () => {
    this.setState(
      {
        query: this.search.value
      },
      () => {
        if (this.state.query && this.state.query.length >= 1) {
          this.getInfo();
        } else if (!this.state.query) {
        }
      }
    );
  };

  handleClick(event) {
    event.preventDefault();
    this.setState(prevState => ({ isFlipped: !prevState.isFlipped }));
  }

  render() {
    var { results } = this.state;
    return (
      <React.Fragment>
        <br />
        <br />
        <form className="col text-center">
          <input
            placeholder="Enter a stock symbol:"
            ref={input => (this.search = input)}
            onChange={this.handleInputChange}
          />
        </form>
        <div className="col text-center">
          e.g., Apple = AAPL ; Tesla = TSLA{" "}
        </div>
        <div>{console.log(results)}</div>
        <div>
          {results.map((item, i, name) => (
            <ul>
              <li key={i}>{results[i.quote]}</li>
            </ul>
          ))}
        </div>
      </React.Fragment>
    );
  }
}

export default Search;
