import React, { Component } from "react";
import NavBar from "./components/navbar";
import Gainers from "./components/gainers";
import Losers from "./components/losers";
import { BrowserRouter } from "react-router-dom";

class App extends Component {
  constructor() {
    super();
    this.state = {
      showComponent: false,
      api: "gainers"
    };
    this.onButtonClick = this.onButtonClick.bind(this);
    this.toggleAPI = this.toggleAPI.bind(this);
  }

  onButtonClick() {
    this.setState(state => ({
      showComponent: !state.showComponent
    }));
  }

  toggleAPI() {
    this.setSate(prevState => {
      if (this.api == prevState) {
        return { api: "losers" };
      } else {
        return { api: "gainers" };
      }
    });
  }

  render() {
    return (
      <BrowserRouter>
        <React.Fragment>
          <NavBar />
          <button onClick={this.toggleAPI}>Gainers or Losers</button>
          <button onClick={this.onButtonClick}>Button</button>
          <div>
            {this.state.showComponent ? (
              <Gainers api={this.state.api} />
            ) : (
              <Losers />
            )}
          </div>
          {this.state.var}
        </React.Fragment>
      </BrowserRouter>
    );
  }
}

export default App;
