import React, { Component } from "react";
import NavBar from "./components/navbar";
import Gainers from "./components/gainers";
import Losers from "./components/losers";
import { BrowserRouter } from "react-router-dom";

class App extends Component {
  constructor() {
    super();
    this.state = {
      showComponent: false,
      api: "gainers"
    };
    this.onButtonClick = this.onButtonClick.bind(this);
  }

  onButtonClick() {
    this.setState(state => ({
      showComponent: !state.showComponent
    }));
  }

  setGainers() {
    this.setState(state => ({ api: "gainers" }, this.otherFunction));
  }

  setLosers() {
    this.setState(state => ({ api: "losers" }, this.otherFunction));
  }
  render() {
    return (
      <BrowserRouter>
        <React.Fragment>
          <NavBar />
          <button onClick={this.setGainers}>Gainers</button>
          <button onClick={this.setLosers}>Losers</button>
          <button onClick={this.onButtonClick}>Button</button>
          <div>
            {this.state.showComponent ? null : <Gainers {...this.state} />}
            {console.log(this.props.showComponent)}
          </div>
        </React.Fragment>
      </BrowserRouter>
    );
  }
}

export default App;
