import React, { Component } from "react";
import ReactCardFlip from "react-card-flip";

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      items: [],
      isLoaded: false,
      isFlipped: false
    };
    this.handleClick = this.handleClick.bind(this);
  }

  componentDidMount() {
    fetch(
      "https://cloud.iexapis.com/beta/stock/market/list/losers?token=pk_b24a0719ee7f4415b33737e51e8ef7f8"
    )
      .then(res => res.json())
      .then(json => {
        this.setState({
          isLoaded: true,
          items: json
        });
      });
  }

  handleClick(e) {
    e.preventDefault();
    this.setState(prevState => ({ isFlipped: !prevState.isFlipped }));
  }

  render() {
    var { isLoaded, items } = this.state;
    if (!isLoaded) {
      return <div>Loading...</div>;
    } else {
      return (
        <div className="App">
          <ul>
            {items.map(item => (
              <ReactCardFlip
                isFlipped={this.state.isFlipped}
                flipDirection="vertical"
              >
                <li key="front">
                  <li key={item.symbol}>
                    Stock: {item.symbol} | Latest Price: {item.latestPrice}
                  </li>

                  <button onClick={this.handleClick}>Click to flip</button>
                </li>
                <li key="back">
                  <li key={item.symbol}>
                    Company Name: {item.companyName} | High: {item.high} | Low:{" "}
                    {item.low}
                  </li>

                  <button onClick={this.handleClick}>Click to flip</button>
                </li>
              </ReactCardFlip>
            ))}
          </ul>
        </div>
      );
    }
  }
}

export default App;
