import React, { Component } from "react";
import CardFlip from "./components/cardflip";
import NavBar from "./components/navbar";

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      items: [],
      isLoaded: false
    };
  }

  componentDidMount() {
    fetch(
      "https://cloud.iexapis.com/beta/stock/market/list/gainers?token=pk_b24a0719ee7f4415b33737e51e8ef7f8"
    )
      .then(res => res.json())
      .then(json => {
        this.setState({
          isLoaded: true,
          items: json
        });
      });
  }

  render() {
    var { isLoaded, items } = this.state;
    if (!isLoaded) {
      return <div>Loading...</div>;
    } else {
      return (
        <React.Fragment>
          <NavBar />
          <main className="container">
            <CardFlip>
              <ul>
                {items.map(item => (
                  <li key={item.symbol}>
                    Stock: {item.symbol} | Latest Price: {item.latestPrice}
                  </li>
                ))}
              </ul>
            </CardFlip>
          </main>
        </React.Fragment>
      );
    }
  }
}

export default App;
