import React, { Component } from "react";
import ReactCardFlip from "react-card-flip";
import NavBar from "./components/navbar";

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      items: [],
      isLoaded: false,
      isFlipped: false
    };
    this.handleClick = this.handleClick.bind(this);
  }

  componentDidMount() {
    fetch(
      "https://cloud.iexapis.com/beta/stock/market/list/gainers?token=pk_b24a0719ee7f4415b33737e51e8ef7f8"
    )
      .then(res => res.json())
      .then(json => {
        this.setState({
          isLoaded: true,
          items: json
        });
      });
  }

  handleClick(event) {
    event.preventDefault();
    this.setState(prevState => ({ isFlipped: !prevState.isFlipped }));
  }

  render() {
    var { isLoaded, items } = this.state;
    if (!isLoaded) {
      return <div>Loading...</div>;
    } else {
      return (
        <React.Fragment>
          <NavBar />
          <main className="container">
            <ul>
              {items.map(item => (
                <ReactCardFlip
                  isFlipped={this.state.isFlipped}
                  flipDirection="vertical"
                >
                  <button onClick={this.handleClick}>Click to flip</button>
                  <li className="card" key="front">
                    <li key={item.symbol}>
                      Stock: {item.symbol} | Latest Price: {item.latestPrice}
                    </li>
                  </li>
                  <li className="card" style={{ width: 18 }} key="back">
                    <li key={item.symbol}>
                      Company Name: {item.companyName} | High: {item.high} |
                      Low: {item.low}
                    </li>
                  </li>
                </ReactCardFlip>
              ))}
              ;
            </ul>
          </main>
        </React.Fragment>
      );
    }
  }
}

export default App;
