import React, { Component } from "react";

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      items: [],
      isLoaded: false
    };
  }

  componentDidMount() {
    fetch(
      "https://cloud.iexapis.com/beta/stock/market/list/losers?token=pk_b24a0719ee7f4415b33737e51e8ef7f8"
    )
      .then(res => res.json())
      .then(json => {
        this.setState({
          isLoaded: true,
          items: json
        });
      });
  }

  render() {
    var { isLoaded, items } = this.state;
    if (!isLoaded) {
      return <div>Loading...</div>;
    } else {
      return (
        <div className="App">
          <ul>
            {items.map(item => {
              <li key={item.symbol}>
                Stock: {item.symbol} | Company Name: {item.companyName} | Latest
                Price: {item.latestPrice}
              </li>;
            })}
            ;
          </ul>
        </div>
      );
    }
  }
}

export default App;
