import React, { Component } from "react";
import { Card, CardBody, CardHeader } from "reactstrap";

const TOKEN = "z0gq5xnnz5l5wuyb1yissddi0khmpaayei3gkrv6";
const BASEURL =
  "https://stocknewsapi.com/api/v1/category?section=general&items=50&token=";

class News extends Component {
  constructor(props) {
    super(props);
    this.state = {
      news: [],
      isNewsLoaded: false
    };
  }

  componentDidMount() {
    fetch(BASEURL + TOKEN)
      .then(results => results.json())
      .then(json => {
        this.setState({
          isNewsLoaded: true,
          news: json
        });
      });
  }

  render() {
    var { isNewsLoaded, news } = this.state;
    if (!isNewsLoaded) {
      return <div>Loading...</div>;
    } else {
      return (
        <React.Fragment>
          <div>
            {news.map(article => (
              <Card>
                <div key="front">
                  <div key={article.title}>
                    <CardBody>
                      <CardHeader>{article.title}</CardHeader>
                      <a href={article.news_url}>Read More</a>
                      <br />
                    </CardBody>
                  </div>
                </div>
              </Card>
            ))}
          </div>
        </React.Fragment>
      );
    }
  }
}

export default News;
