import React, { Component } from "react";
import { Card, CardBody, CardHeader } from "reactstrap";
import styles from "../index.css";

const TOKEN = "z0gq5xnnz5l5wuyb1yissddi0khmpaayei3gkrv6";
const BASEURL =
  "https://stocknewsapi.com/api/v1/category?section=general&items=50&token=";

class News extends Component {
  constructor() {
    super();
    this.state = {
      articles: [],
      isNewsLoaded: false
    };
  }

  componentDidMount() {
    fetch(BASEURL + TOKEN)
      .then(results => results.json())
      .then(json => {
        this.setState({
          isNewsLoaded: true,
          articles: json
        });
      });
  }

  render() {
    var { isNewsLoaded, articles } = this.state;

    if (!isNewsLoaded) {
      return <div>Loading...</div>;
    } else {
      return (
        <React.Fragment>
          <main className={styles.newsCard}>
            <div className={styles.newsCard}>
              {articles.data.map(article => (
                <Card className={styles.newsCard}>
                  <div key={article.title}>
                    <CardBody className={styles.newsCard}>
                      <CardHeader className={styles.newsCard}>
                        {article.title}
                      </CardHeader>
                      <a href="">Read More</a>
                      <br />
                    </CardBody>
                  </div>
                </Card>
              ))}
            </div>
          </main>
        </React.Fragment>
      );
    }
  }
}

export default News;
