import React, { Component } from "react";
import ReactCardFlip from "react-card-flip";
import styles from "../index.css";
import { Card, CardBody, Button, CardHeader } from "reactstrap";
import Suggestions from "./suggestions";
import axios from "axios";

const BASEURL = "https://cloud.iexapis.com/v1/stock/";
const TOKEN = `${process.env.REACT_APP_IEX_KEY}`;

class Search extends Component {
  state = {
    query: "",
    results: []
  };

  getInfo = () => {
    let stockArray = [];
    fetch(BASEURL + `${this.state.query}` + "/quote" + TOKEN)
      .then(res => res.json())
      .then(json => {
        let toString = JSON.stringify(json);
        let toArray = $.parseJSON("[" + toString + "]");
        this.setState({
          results: toArray
        });
      });
  };

  handleInputChange = () => {
    this.setState(
      {
        query: this.search.value
      },
      () => {
        if (this.state.query && this.state.query.length >= 1) {
          if (this.state.query.length % 2 == 0) {
            this.getInfo();
          }
        } else if (!this.state.query) {
        }
      }
    );
  };

  render() {
    var { results } = this.state;
    return (
      <React.Fragment>
        <form>
          <input
            placeholder="Enter a stock symbol:"
            ref={input => (this.search = input)}
            onChange={this.handleInputChange}
          />
        </form>
        <div>{console.log(results)}</div>
      </React.Fragment>
    );
  }
}

export default Search;
