import React, { Component } from "react";
import XMLParser from "react-xml-parser";

const URL = "https://finance.yahoo.com/rss/topstories";

class News extends Component {
  constructor(props) {
    super(props);
    this.state = {
      items: [],
      isLoaded: false
    };
  }

  componentDidMount() {
    fetch(URL).then(results => {
      results.text().then(str => {
        var res = new XMLParser().parseFromString(str);
        res
          .then(res => res.json())
          .then(json => {
            this.setState({
              isLoaded: true,
              items: json
            });
          });
      });
    });
  }

  render() {
    var { isLoaded, items } = this.state;
    if (!isLoaded) {
      return <div>Loading...</div>;
    } else {
      return <div> {console.log(this.state.items)} </div>;
    }
  }
}

export default News;
