import React, { Component } from "react";
import XMLParser from "react-xml-parser";

const URL = "http://finance.yahoo.com/rss/headline?s=yhoos";

class News extends Component {
  constructor() {
    super();
    this.state = {
      news: [],
      isNewsLoaded: false
    };
  }

  componentDidMount() {
    fetch(URL).then(results => {
      results.text().then(str => {
        var res = new XMLParser().parseFromString(str);
        res
          .then(res => res.json())
          .then(json => {
            this.setState({
              isNewsLoaded: true,
              news: json
            });
          });
      });
    });
  }

  render() {
    var { isNewsLoaded, news } = this.state;
    if (!isNewsLoaded) {
      return <div>Loading...</div>;
    } else {
      return <div> {console.log(this.state.news)} </div>;
    }
  }
}

export default News;
