import React, { Component } from "react";
import ReactCardFlip from "react-card-flip";
import styles from "../index.css";
import { Card, CardBody, Button, CardHeader } from "reactstrap";
import Suggestions from "./suggestions";
import axios from "axios";

const BASEURL = "https://cloud.iexapis.com/v1/stock/";
const TOKEN = `${process.env.REACT_APP_IEX_KEY}`;

class Search extends Component {
  state = {
    query: "",
    results: []
  };

  getInfo = () => {
    axios
      .get(BASEURL + `${this.state.query}` + "/quote" + TOKEN)
      .then(({ data }) => {
        this.setState({
          results: data
        });
      });
  };

  handleInputChange = () => {
    this.setState(
      {
        query: this.search.value
      },
      () => {
        if (this.state.query && this.state.query.length > 1) {
          if (this.state.query.length % 2 === 0) {
            this.getInfo();
          }
        } else if (!this.state.query) {
        }
      }
    );
  };

  render() {
    var { results } = this.state;
    return (
      <React.Fragment>
        <form>
          <input
            placeholder="Enter a stock symbol:"
            ref={input => (this.search = input)}
            onChange={this.handleInputChange}
          />
        </form>
        <div>{console.log(results)}</div>

        <div>
          {results.json.map(item => (
            <Card>
              <ReactCardFlip
                isFlipped={this.state.isFlipped}
                flipDirection="vertical"
                flipSpeedBackToFront={1}
                flipSpeedFrontToBack={1}
              >
                <div key="front">
                  <div key={item.symbol}>
                    <CardBody>
                      <CardHeader>{item.symbol}</CardHeader>
                      Latest Price: ${item.latestPrice.toFixed(2)}
                      <br />
                      Change: ${item.change.toFixed(2)}
                      <br />% Change: &nbsp;
                      {(item.changePercent * 100).toFixed(2)}%
                      <br />
                      High: ${item.high.toFixed(2)} <br /> Low: $
                      {item.low.toFixed(2)}
                      <br />
                    </CardBody>
                  </div>
                </div>
              </ReactCardFlip>
            </Card>
          ))}
        </div>
      </React.Fragment>
    );
  }
}

export default Search;
