import React, { Component } from "react";
import { Card, CardBody, CardHeader } from "reactstrap";

const TOKEN = "z0gq5xnnz5l5wuyb1yissddi0khmpaayei3gkrv6";
const BASEURL =
  "https://stocknewsapi.com/api/v1/category?section=general&items=50&token=";

class News extends Component {
  constructor() {
    super();
    this.state = {
      articles: [],
      isNewsLoaded: false
    };
  }

  componentDidMount() {
    fetch(BASEURL + TOKEN)
      .then(results => results.json())
      .then(json => {
        this.setState({
          isNewsLoaded: true,
          articles: json
        });
      });
  }

  render() {
    var { isNewsLoaded, articles } = this.state;

    if (!isNewsLoaded) {
      return <div>Loading...</div>;
    } else {
      return (
        <React.Fragment>
          <main>
            <div>
              {articles.data.map(article => (
                <Card>
                  <div key={article.title}>
                    <CardBody>
                      <img src={article.image_url} href={article.news_url} />
                      <CardHeader>{article.title}</CardHeader>
                      <a href={article.news_url}>Read More</a>
                      <br />
                      {article.date}
                      <br />
                      Source: {article.source_name}
                    </CardBody>
                  </div>
                </Card>
              ))}
            </div>
          </main>
        </React.Fragment>
      );
    }
  }
}

export default News;
